var link=document.querySelector(".feedback-button");var popup=document.querySelector(".feedback");var close=document.querySelector(".modal-close");var nameFeedback=document.querySelector("[name=login-feedback]");var emailFeedback=document.querySelector("[name=email-feedback]");var form=document.querySelector(".feedback-form");var wrapper=document.querySelector(".popup-wrapper");var isStorageSupport=!0;var storageName="";var storageEmail="";try{storageName=localStorage.getItem("nameFeedback");storageEmail=localStorage.getItem("emailFeedback")}catch(err){isStorageSupport=!1}
link.addEventListener("click",function(evt){evt.preventDefault();wrapper.classList.add("wrapper-show");popup.classList.add("modal-show");if(storageName){nameFeedback.value=storageName}else{nameFeedback.focus()}
if(storageEmail){storageEmail.value=storageEmail}else if(storageName){emailFeedback.focus()}});close.addEventListener("click",function(evt){evt.preventDefault();wrapper.classList.remove("wrapper-show");popup.classList.remove("modal-show");popup.classList.remove("modal-error")})
form.addEventListener("submit",function(evt){if(!nameFeedback.value||!emailFeedback.value){evt.preventDefault();popup.classList.remove("modal-error");popup.offsetWidth=popup.offsetWidth;popup.classList.add("modal-error")}else{if(isStorageSupport){localStorage.setItem("nameFeedback",nameFeedback.value)}}})
window.addEventListener("keydown",function(evt){if(evt.keyCode===27){evt.preventDefault();if(popup.classList.contains("modal-show")){wrapper.classList.remove("wrapper-show");popup.classList.remove("modal-show");popup.classList.remove("modal-error")}}})